import React, { Component } from 'react';
import NumberPad from './NumberPad';

import './App.css';

class App extends Component {
  constructor(props) {
    super(props);
    this.state ={
      calcDisplay: 0,
      numInMem: 0,
      operator: "",

    }
  }

  numberClick(e) {
    const digit = e.target.value;
    console.log(digit)
  }

  render() {
    return (
      <div className="App">
        <header className="App-header">
          <h1 className="display-box" id="title">THE HALCULATOR</h1>
          <NumberPad
          numberClick={this.numberClick}
          />

        </header>
      </div>
    );
  }
}

export default App;
